import React,{ useState,useEffect} from 'react'
import './Flight.css'
import Axios from "axios";

const Flight = () => {

 const[allflights,setAllflights]=useState([])
 const[searchterm,setsearch]=useState([])

  useEffect(() =>{
    Axios.get("http://localhost:3001/").then((Response)=> {
      setAllflights(Response.data)
    })
  
  },[]);

  const deleteFlight = (id)=>{
    Axios.delete(`http://localhost:3001/delete/${id}`)
  };

  return (<div className="app-container">
    <h1> Flights</h1>
    <table>
    <tbody>  
     <tr>
       <th>flight Number</th>
       <th>Departure Date</th>
       <th>Departure time</th>
       <th>Departure Airport</th>
       <th>Arrival Date</th>
       <th>Arrival time</th>
       <th>Arrival Airport</th>
       <th>Economy Class Seats</th>
       <th>Business Class Seats</th>
       <th>Actions</th>
       
       </tr>
     
      <tr>
      <td><input type="Number" onChange={(event)=> {setsearch(event.target.value); }} /></td> 
      <td><input type="Date" onChange={(event)=> {setsearch(event.target.value); }} /></td> 
      <td><input type="String" onChange={(event)=> {setsearch(event.target.value); }} /></td> 
      <td><input type="String" onChange={(event)=> {setsearch(event.target.value); }} /></td> 
      <td><input type="Date" onChange={(event)=> {setsearch(event.target.value); }}  /></td> 
      <td><input type="String" onChange={(event)=> {setsearch(event.target.value); }}/></td> 
      <td><input type="String" onChange={(event)=> {setsearch(event.target.value); }} /></td> 
      <td><input type="Number" onChange={(event)=> {setsearch(event.target.value); }} /></td> 
      <td><input type="Number"  onChange={(event)=> {setsearch(event.target.value); }}/></td> 
      <td></td>
      
      
      </tr>
    
      
   {allflights.filter((val)=> {
if(val.Flight_Number == searchterm  && val.Departure_Date.includes(searchterm) && val.Departure_Time.includes(searchterm)
       && val.Departure_Airport.includes(searchterm)   ){
        return val
     }
   })
   .map((val,key)=>{
     return (<tr> 
       <td>{val.Flight_Number }</td> 
       <td>{val.Departure_Date} </td> 
       <td>{val.Departure_Time }</td>
       <td>{val.Departure_Airport} </td>  
       <td>{val.Arrival_Date} </td> 
       <td>{val.Arrival_Time} </td> 
       <td>{val.Arrival_Airport} </td> 
       <td>{val.Number_of_Economy_seats} </td> 
       <td>{val.Number_of_Business_seats} </td> 
       <td>  <button>Edit</button>
       <button onClick={()=> deleteFlight(val._id)}>Delete</button>
       </td> 
       </tr>
       )
   })}

   
   
   </tbody>
  </table>
  </div>
  )
}

export default Flight
