import React, { useState } from 'react'
export const Search = ({ handleSearchTermsChange }) => {
  const [searchFormData, setSearchFormData] = useState(
    {
      flightNumber: "",
      departureDate: "",
      departureTime: "",
      arrivalDate: "",
      arrivalTime: "",
      departureAirport: "",
      arrivalAirport: "",
    })

  const handleSearchFormChange = (event) => {
    event.preventDefault();
    const fieldName = event.target.getAttribute("name");
    const fieldValue = event.target.value;
    const newFormData = { ...searchFormData };
    newFormData[fieldName] = fieldValue;
    setSearchFormData(newFormData);
  }

  const select_filter = () => {
    handleSearchTermsChange({ searchFormData });
  }

  return (

    <div>
      <input name="flightNumber" placeholder="Flight Number" onChange={(event) => { { handleSearchFormChange(event.target.value); } }} />
      <input name="departureDate" placeholder="Departure Date" onChange={(event) => { { handleSearchFormChange(event.target.value); } }} />
      <input name="departureTime" placeholder="Departure Time" onChange={(event) => { { handleSearchFormChange(event.target.value); } }} />
      <input name="departureAirport" placeholder="Departure Airport" onChange={(event) => { { handleSearchFormChange(event.target.value); } }} />
      <input name="arrivalDate" placeholder="Arrival Date" onChange={(event) => { { handleSearchFormChange(event.target.value); } }} />
      <input name="arrivalTime" placeholder="Arrival Time" onChange={(event) => { { handleSearchFormChange(event.target.value); } }} />
      <input name="arrivalAirport" placeholder="Arrival Airport" onChange={(event) => { { handleSearchFormChange(event.target.value); } }} />
      <button onClick={select_filter}>filter</button>
    </div>
  )
}
