      Flight_Number: newNumber,
      Deptarture_Date: newDepDate,
      Depature_Time: newDepTime,
      Arrival_Date: newArrivalDate,
      Arrival_Time: newArrivalTime,
      Departure_Airport: newDepAirport,
      Arrival_Airport: newArrivalAirport,
      Number_of_Economy_Seats: newEcoSeats,
      Number_of_Business_Seats: newBusinessSeats
    })
  }
  return (
    <tr>
      <td>
        <input type="text" name="Flight_Number" placeholder="Flight Number" value={editFormData.Flight_Number}
          onChange={(event) => {
            handleEditFormChange(event);
          }} />
      </td>
      <td>
        <input type="date" name="Departure_Date" placeholdeer="" Defaultvalue={editFormData.Departure_Date} onChange={(event) => {
          handleEditFormChange(event);
        }} />

      </td>
