import React, { useState, useEffect } from 'react'
import './Flight.css'
import Axios from "axios";
import { ReadableRow } from './ReadableRow';
import { EditFlightRow } from './EditFlightRow';
import { Fragment } from 'react';
import { Popup } from './Popup.ks';

export default function Flight() {
  const [allflights, setAllflights] = useState([]);
  const [EditedFlightId, setEditingFlightId] = useState(null);
  const [toBeDeletedFlight, setDeletedFlight] = useState([]);
  const [popupbutton,setpopupbutton] = useState(false);
  const [editFormData, setEditFormData] = useState(
    {
      Flight_Number: "",
      Departure_Date: "",
      Departure_Time: "",
      Arrival_Date: "",
      Arrival_Time: "",
      Departure_Airport: "",
      Arrival_Airport: "",
      Number_of_Economy_Seats: "",
      Number_of_business_Seats: ""

    })
  const handleEditClick = (event, val) => {
    event.preventDefault();
    setEditingFlightId(val._id);
    const formValues =
    {
      Flight_Number: val.Flight_Number,
      Departure_Date: val.Departure_Date,
      Departure_Time: val.Departure_Time,
      Arrival_Date: val.Arrival_Date,
      Arrival_Time: val.Arrival_Time,
      Departure_Airport: val.Departure_Airport,
      Arrival_Airport: val.Arrival_Airport,
      Number_of_Economy_Seats: val.Number_of_Economy_Seats,
      Number_of_business_Seats: val.Number_of_business_Seats
    }
    setEditFormData(formValues);
  }

  const handleEditFormChange = (event) => {
    event.preventDefault();
    const fieldName = event.target.getAttribute("name");
    const fieldValue = event.target.value;
    const newFormData = { ...editFormData };
    newFormData[fieldName] = fieldValue;
    setEditFormData(newFormData);
  }
  const handleDeleteClick = (val) => {
    setpopupbutton(true);
    setDeletedFlight(val);
  }
  
  useEffect(() => { Axios.get("http://localhost:3001/").then((Response) => setAllflights(Response.data)) }, []);

  return (
    <div className="app-container">
      {/* <div> <h1> Flights</h1></div> */}
      <table>
        <thead>
          <tr>
            <th>Flight Number</th>
            <th>Departure Date</th>
            <th>Departure time</th>
            <th>Departure Airport</th>
            <th>Arrival Date</th>
            <th>Arrival time</th>
            <th>Arrival Airport</th>
            <th>Economy Class Seats</th>
            <th>Business Class Seats</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {allflights.map(val => (
            <Fragment>
              {(EditedFlightId === val._id) ? (
                <EditFlightRow editFormData={editFormData} handleEditFormChange={handleEditFormChange} id={vaid} />) :
                (<ReadableRow val={val} handleEditClick={handleEditClick} handleDeleteClick={handleDeleteClick} />)}
            </Fragment>))}
        </tbody>
      </table>
      <Popup trigger={popupbutton} setTrigger={setpopupbutton} delete_flight={toBeDeletedFlight} ><h2>Are you sure you want to delete the following flight:</h2>
        <h3>Flight Number:{delete_flight.Flight_Number}</h3>
        <h3>Departure Date: {delete_flight.Departure_Date}  </h3>
        <h3>Departure Time: {delete_flight.Departure_Date}  </h3>
        <h3>Departure Airport: {delete_flight.Departure_Airport}  </h3>
        <h3>Arrival Date: {delete_flight.Arrival_Date}  </h3>
        <h3>Arrival Time: {delete_flight.Arrival_Time}  </h3>
        <h3>Arrival Airport: {delete_flight.Arrival_Airport}  </h3>
        <h3>Number of Economy seats: {delete_flight.Number_of_Economy_seats}  </h3>
        <h3>Number of Business seats: {delete_flight.Number_of_Business_seats}  </h3></Popup>
    </div>

  )

}
