import React, { useState, useEffect } from 'react'
import './Popup.css'
import Axios from "axios";

export default function Popup(props) {
  deleteFlight = (id) => {
    Axios.delete(`http://localhost:3001/delete/${id}`);
  };

  return (props.trigger) ? (
    <div className="popup">
      <div className="popup-inner">
        <button className="close-btn1" onClick={() => deleteFlight(props.delete_flight._id)}> confirm </button>
        <button className="close-btn" onClick={() => props.setTrigger(false)}>cancel</button>
        {props.children}
        <h2>Are you sure you want to delete the following flight:</h2>
        <h3>Flight Number:{delete_flight.Flight_Number}</h3>
        <h3>Departure Date: {delete_flight.Departure_Date}  </h3>
        <h3>Departure Time: {delete_flight.Departure_Date}  </h3>
        <h3>Departure Airport: {delete_flight.Departure_Airport}  </h3>
        <h3>Arrival Date: {delete_flight.Arrival_Date}  </h3>
        <h3>Arrival Time: {delete_flight.Arrival_Time}  </h3>
        <h3>Arrival Airport: {delete_flight.Arrival_Airport}  </h3>
        <h3>Number of Economy seats: {delete_flight.Number_of_Economy_seats}  </h3>
        <h3>Number of Business seats: {delete_flight.Number_of_Business_seats}  </h3>
      </div>
    </div>
  ) : ("");

    
}
